        }

        // Check if class details found for the provided ID
        if (!$classDetails) {
            // Handle 404 error: return appropriate view with error message
            return view('error', ['error' => 'Class not found.']);
        }

        // Extracting class names
        $classNames = collect($courseData)->flatMap(function ($course) {
            return collect($course['classes'])->pluck('class_name');
